const fixtime = theme.global.fix_time || 1000;

function onPageView() {
    const targets = document.querySelector('.social-contacts');
    if (targets && location.pathname.startsWith(`${config.root}page/`)){
        setTimeout(() => {
            targets.scrollIntoView({ behavior: 'smooth' });
        }, fixtime);
    } else {
        requestAnimationFrame(onPageView);
    }
}

window.onload = function() { 
    try { swup.hooks.on("page:view", onPageView);
    } catch (e) {}
};

document.addEventListener("DOMContentLoaded", onPageView);